mapper.select:
select *
from tab_dorm
where dbid in (select dbid from tab_dorm_building
where 1=1

    and dbname=#{dormBuildingName}

)

    and dname like concat('%', #{searchContent}, '%')

limit #{start},#{size}

select count(*) from tab_dorm where dbid in (select dbid from tab_dorm_building where 1=1
and dbname=#{dormBuildingName}
)
and dname like concat('%', #{searchContent}, '%')
