mapper.select:
select *
from tab_late
where did in (select did from tab_dorm
where dbid in (select dbid from tab_dorm_building
where 1=1

    and dbname=#{dormBuildingName}

)

    and dname=#{dormName}

)

    and lname like concat('%', #{searchContent}, '%')

limit #{start},#{size}

select count(*)
from tab_late
where did in (select did from tab_dorm
where dbid in (select dbid from tab_dorm_building
where 1=1

    and dbname=#{dormBuildingName}

)

    and dname=#{dormName}

)

    and lname like concat('%', #{searchContent}, '%')
