mapper.select:
select *
from tab_manager
where dbid in (select dbid from tab_dorm_building
where 1=1

    and dbname=#{dormBuildingName}

)

    and ((name like concat('%', #{searchContent}, '%'))
    or (username like concat('%', #{searchContent}, '%')))

limit #{start},#{size}

select count(*) from tab_manager where dbid in (select dbid from tab_dorm_building where 1=1
and dbname=#{dormBuildingName}
)
and ((name like concat('%', #{searchContent}, '%')) or (username like concat('%', #{searchContent}, '%')))
