mapper.select:
select * from tab_student
where did in (select did from tab_dorm
where dbid in (select dbid from tab_dorm_building
where 1=1

    and dbname=#{dormBuildingName}

)

    and dname=#{dormName}

)

    and ((sname like concat('%', #{searchContent}, '%'))
    or (grade like concat('%', #{searchContent}, '%'))
    or (stu_number like concat('%', #{searchContent}, '%'))
    or (teacher_name like concat('%', #{searchContent}, '%')))

limit #{start}, #{size}

select count(*) from tab_student
where did in (select did from tab_dorm
where dbid in (select dbid from tab_dorm_building
where 1=1

    and dbname=#{dormBuildingName}

)

    and dname=#{dormName}

)

    and ((sname like concat('%', #{searchContent}, '%'))
    or (grade like concat('%', #{searchContent}, '%'))
    or (stu_number like concat('%', #{searchContent}, '%'))
    or (teacher_name like concat('%', #{searchContent}, '%')))
